create table book(isbn varchar(255), title varchar(255) not null, author varchar(255) not null, category varchar(255) not null, lender_userid integer, primary key (isbn));
create table user(userid integer generated by default as identity, username varchar(255) not null unique, password varchar(255) not null, primary key (userid));
create table statistic(statisticid integer generated by default as identity, count integer not null, category varchar(255) not null, primary key (statisticid));